mapper.select:
SELECT 

FROM lzr_message_demo a

WHERE a.id = #{id}

SELECT 

FROM lzr_message_demo a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.demo_name LIKE concat('%',#{demoName},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM lzr_message_demo a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE lzr_message_demo SET 	
	demo_name  =#{demoName},
	demo_content  =#{demoContent},
	update_date  =#{updateDate}
WHERE id = #{id}

UPDATE lzr_message_demo SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
