mapper.select:
SELECT 

FROM lzr_friendly_link a

WHERE a.id = #{id}

SELECT 

FROM lzr_friendly_link a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE lzr_friendly_link SET 	
	friendly_link_url=#{friendlyLinkUrl},
	friendly_desc =##{friendlyDesc},
