mapper.select:
SELECT 

FROM lzr_order a

WHERE a.id = #{id}

SELECT 

FROM lzr_order a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.product_name LIKE concat('%',#{productName},'%')




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM lzr_order a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.create_date   >= #{createDate}

SELECT 

FROM lzr_order a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE lzr_order SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE lzr_order SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id in

		  #{id}
