mapper.select:
SELECT 

FROM lzr_hot_search a

WHERE a.id = #{id}

SELECT 

FROM lzr_hot_search a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.city_id =#{cityId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM lzr_hot_search a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE lzr_hot_search SET 	
	city_name = #{cityName},
	city_id = #{cityId},
	key_name = #{keyName},
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE lzr_hot_search SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
