mapper.select:
SELECT 

FROM lzr_service_area a

WHERE a.id = #{id}

SELECT 

FROM lzr_service_area a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.type =#{type}


		AND a.province_id =#{provinceId}


		AND a.city_id =#{cityId}


		AND a.zone_id =#{zoneId}


		AND a.open_status =#{openStatus}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM lzr_service_area a


	a.del_flag = #{DEL_FLAG_NORMAL}
		AND a.type in ('3','5') 

		AND a.province_id =#{provinceId}


		AND a.city_id =#{cityId}


		AND a.zone_id =#{zoneId}


		AND a.open_status =#{openStatus}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM lzr_service_area a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id != #{id}


		AND a.province_id =#{provinceId}


		AND a.city_id =#{cityId}


		AND a.zone_id =#{zoneId}


		AND a.area_name =#{areaName}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE lzr_service_area SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE lzr_service_area SET 
	open_status = #{openStatus},
	open_date = #{openDate},
	update_date = #{updateDate}
WHERE id = #{id}
