mapper.select:
SELECT 

FROM lzr_product_assort a

WHERE a.id = #{id}

SELECT 

FROM lzr_product_assort a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.product_id =#{productId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM lzr_product_assort a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE lzr_product_assort SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
