mapper.select:
SELECT 

FROM lzr_product_block a

WHERE a.id = #{id}

SELECT 

FROM lzr_product_block a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.id =#{id}


		AND a.block_name LIKE concat('%',#{blockName},'%')


		AND a.update_date  >= #{updateStartDate}


		AND a.update_date  <= #{updateEndDate}





		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE lzr_product_block SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
