mapper.select:
SELECT 

FROM lzr_product_comment a

WHERE a.id = #{id}

SELECT 

FROM lzr_product_comment a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.member_name LIKE concat('%',#{memberName},'%')


		AND a.id =#{id}


		AND a.comment_date  >= #{commentDateStart}


		AND a.comment_date  <= #{commentDateEnd}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM lzr_product_comment a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE lzr_product_comment SET 	
	comment_status =#{commentStatus},
	update_date =#{updateDate}
WHERE id = #{id}

UPDATE lzr_product_comment SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
