mapper.select:
SELECT 

FROM lzr_product_pic a

WHERE a.id = #{id}

SELECT 

FROM lzr_product_pic a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.pic_type =#{picType}


		AND a.pic_relate_id =#{picRelateId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE lzr_product_pic SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE lzr_product_pic SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id in

		  #{id}
