mapper.select:
SELECT 

FROM lzr_product_room a

WHERE a.id = #{id}

SELECT 

FROM lzr_product_room a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.Room_type =#{RoomType}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE lzr_product_room SET 	
	room_type =#{roomType},
    room_name =#{roomName},
	update_date =#{updateDate}
WHERE id = #{id}

UPDATE lzr_product_room SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

