mapper.select:
SELECT 

FROM lzr_trade_deposit a

WHERE a.id = #{id}

SELECT 

FROM lzr_trade_deposit a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.trade_code =#{tradeCode}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM lzr_trade_deposit a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE lzr_trade_deposit SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
